 console.log(event.target.value)
  };


  render() {
    return (
      <div>
        <Jumbotron />
        <div className="container">
          <div className="row">
            <div className="col">
              {this.state.imgCast.map((item,i) => {

                return (
                  <ImageThumbnail onClick={this.log} value={item.value} key={item.value} idTag={i} src={item.src} />
                )
              })}

            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;
