import Jumbotron from './components/Jumbotron';
import ImageThumbnail from './components/ImageThumbnail';
import React, { Component } from "react";

const data = [{ key: "1", src: "../images/arya.jpeg" },
{ key: "2", src: "../images/bran.jpeg" },
{ key: "3", src: "../images/brienne.jpeg" },
{ key: "4", src: "../images/catelyn.jpeg" },
{ key: "5", src: "../images/cersei.jpeg" },
{ key: "6", src: "../images/daenerys.jpeg" },
{ key: "7", src: "../images/jaime.jpeg" },
{ key: "8", src: "../images/jaqen.jpeg" },
{ key: "9", src: "../images/joffrey.jpeg" },
{ key: "10", src: "../images/jon.jpeg" },
{ key: "11", src: "../images/khal.jpeg" },
{ key: "12", src: "../images/Ned.jpeg" }];



function ranImg() {
  const array = [];
  for (let x = 0; x < data.length; x++) {
   let foundspot = false;
    while (foundspot) {
      console.log("here")
      let rannum = Math.floor(Math.random() * data.length);
      if (array[rannum] === "undefined") {
        array[rannum].push(data[x])
        foundspot=true;
      }
    }
  }

  return array;
}

class App extends Component {

  state = {
    imgCast: ranImg()
  }

  render() {
    return (
      <div>
        <Jumbotron />
        <div className="container">
          <div className="row">
            <div className="col">
              {this.state.imgCast.map((item, i) => {
                return (
                  <ImageThumbnail key={item.key} src={item.src} />
                )
              })}

            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;
