import Jumbotron from './components/Jumbotron';
import ImageThumbnail from './components/ImageThumbnail';
import React, { Component } from "react";


const data = [{value:"1",src: "../images/arya.jpeg" },
{value:"2", src: "../images/bran.jpeg" },
{value:"3", src: "../images/brienne.jpeg" },
{value:"4", src: "../images/catelyn.jpeg" },
{value:"5", src: "../images/cersei.jpeg" },
{value:"6", src: "../images/daenerys.jpeg" },
{value:"7", src: "../images/jaime.jpeg" },
{value:"8", src: "../images/jaqen.jpeg" },
{value:"9", src: "../images/joffrey.jpeg" },
{value:"10", src: "../images/jon.jpeg" },
{value:"11", src: "../images/khal.jpeg" },
{value:"12", src: "../images/Ned.jpeg" }];


class App extends Component {

  state = {
    imgCast: data,
    picked:[]
  }

  array = (item) =>{
   this.setState({picked:[item]})
   console.log(this.state.picked)
  }

  log = event => {
    event.preventDefault();

    this.array(event.target.attributes.value.value)

  };


  render() {
    return (
      <div>
        <Jumbotron />
        <div className="container">
          <div className="row">
            <div className="col">
              {this.state.imgCast.map((item, i) => {

                return (
                  <ImageThumbnail onClick={this.log} value={item.value} key={item.value} idTag={i} src={item.src} />
                )
              })}

            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default App;
